inline void wifiSearching()
{
  static int frame = 0;
  display.clearDisplay();
  display.drawBitmap(32, 0, wifi_search_bits[frame], FRAME_WIDTH, FRAME_HEIGHT, 1);
  display.display();
  frame = (frame + 1) % FRAME_COUNT;
}

inline void wifiSuccess()
{
  display.clearDisplay();
  display.drawBitmap(16, -1, wifi_succ_bits, 96, 62, 1);
  display.display();
}

inline void wifiFail()
{
  display.clearDisplay();
  display.drawBitmap(14, -11, wifi_fail_bits, 100, 86, 1);
  display.display();
  delay(2000);
  display.clearDisplay();
}

